import { LightningElement, api, wire } from "lwc";
import { ShowToastEvent } from "lightning/platformShowToastEvent";

import Applicant_s_Name__c from "@salesforce/schema/Job_Application__c.Applicant_s_Name__c";
import Email__c from "@salesforce/schema/Job_Application__c.Email__c";
import Experrience__c from "@salesforce/schema/Job_Application__c.Experrience__c";
import Mobile_No__c from "@salesforce/schema/Job_Application__c.Mobile_No__c";
import Skills__c from "@salesforce/schema/Job_Application__c.Skills__c";

let logo_list = ["https://www.freepnglogos.com/uploads/company-logo-png/company-logo-transparent-png-19.png", "data:image/jpeg;base64,", "data:image/png;base64,", "data:image/jpeg;base64,", "data:image/jpeg;base64,", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT_jC1_2nxKdM4hofYqOcMKhY1H-f0SCHysLw&usqp=CAU", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTxS51KGPwKI6d_ifpaWu-KDSS0VDrUa6WsUA&usqp=CAU", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQT_A9jbaWm3l99Yqifa_quHbw-C0wsvprqt8hp6-3YFvwmQDIK3pgNlhIYPR214gOWT_U&usqp=CAU", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTYWcoWOkPNbDk_mdQ1rExjZgSgDtL2kgJ2oJ44Eqh-OmLSZy9ASKpWBRyZM8XXilO25aw&usqp=CAU", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSbj70dRumwRpykkLTilfgwzXX05zocEfvQyg&usqp=CAU"];

export default class job_description extends LightningElement {

    @api job_object;
    fieldList = [Applicant_s_Name__c, Email__c, Experrience__c, Mobile_No__c, Skills__c];

    isLoaded = false;
    pickLOGO;
    renderedCallback() {
        if (this.isLoaded) {
            return
        }

        this.template.querySelector('.job_description').innerHTML = this.job_object.description;
        this.pickLOGO = logo_list[Math.floor(Math.random() * logo_list.length)];
        this.isLoaded = true;
    }

    handleBack() {
        this.dispatchEvent(new CustomEvent("eventfromdescription", { detail: "null" }));
    }

    handleClickCompany() {
        window.open(this.job_object.company_url, "_blank");
    }

    form = false;
    handleApplyJob() {
        this.form = true;
    }

    successHandler(event) {
        this.dispatchEvent(new ShowToastEvent({ title: "Successfully Applied for JOB", message: `Find the reference ID: ${event.detail.id} :)`, variant: "success" }));
    }

    closeModel() {
        this.form = false;
    }
}